import { BrowserRouter, Route } from 'react-router-dom';import Header from './components/Header';
import Container from './components/Container';
import './style/App.css';
import Element from "./sort/Element";
import ElementMakeType from "./sort/ElementMakeType";
import SortFactory from "./sort/SortFactory";
import SortType from "./sort/SortType";

class RandomPage extends React.Component {
    sortFactory = new SortFactory();
    sortInstance;

    constructor(props) {
        super(props);

        const length = 100;
        let element = new Element();
        element.initElements(length, ElementMakeType.RANDOM);

        this.state = {
            length,
            element,    //여기에 arr 저장해야함
            sortType: SortType.Bubble,
            elementType: ElementMakeType.RANDOM,
            delay: 10,
            //list: []
        }
    }

    handleArray = (list) => {
        console.log(list);
        this.setState({
            list,
        }, this.remakeFileList);

    }

    /*handleLength = (length) => {
        console.log(length);
        this.setState({
            length,
        }, this.remakeHeightList);
    }*/

    remakeFileList = () => {
        this.handleStop();
        const {
            element,
            length,
            elementType,
            list
        } = this.state;
        element.initElements(length, elementType, list);
        this.setState({element});
    }

    remakeHeightList = () => {
        this.handleStop();
        const {
            element,
            length,
            elementType
        } = this.state;
        element.initElements(length, elementType);
        this.setState({element});
    }

    updateElementArr = (arr) => {
        const element = this.state.element;
        element.arr = arr;
        this.setState({
            element
        });
    }

    handleSorting = () => {
        const {
            sortType,
            delay
        } = this.state;
        this.sortInstance = this.sortFactory.getSort(sortType, this.state.element.arr, delay, this.updateElementArr, this.handleSorting);
        this.handleStop();
        this.sortInstance.start();
    }

    handleStop = () => {
        if(this.sortInstance) {
            this.sortInstance.stop();
        }
    }

    /*handleDelay = (delay) => {
        this.handleStop();
        this.setState({
            delay
        });
    }*/

    handleSortType = (sortType) => {        //Bubble, Quick..
        this.handleStop();
        this.setState({
            sortType
        }, this.remakeHeightList);
    }

    handleElemType = (elementType) => {
        this.handleStop();
        this.setState({
            elementType
        }, this.remakeHeightList);
    }

    render() {
        const {
            sorting,
            length,
            delay,
            element,
            sortType,
            elementType     //random
        } = this.state;

        const {
            handleLength,
            handleSorting,
            handleStop,
            handleDelay,
            handleSortType,
            remakeHeightList,
            handleElemType
        } = this;

        return (
            <div className="App">
                <Header/>
                <Container
                    {...this.state}
                    {...this}
                />
            </div>
        );
    }
}

export default RandomPage;
