//import React from 'react';
import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Header from '../filecomponents/Header';
import Container from '../filecomponents/Container';
import '../style/App.css';
import Element from "../filesort/Element";
import ElementMakeType from "../filesort/ElementMakeType";
import SortFactory from "../filesort/SortFactory";
import SortType from "../filesort/SortType";

class FilePage extends Component {

    sortFactory = new SortFactory();
    sortInstance;

    constructor(props) {
        super(props);
        const length = 100;
        this.arr = null;
        let element = new Element();
        element.initElements(length, ElementMakeType.RANDOM);

        this.state = {
            length,
            //element,
            sortType: SortType.Bubble,
            //elementType: ElementMakeType.RANDOM,
            delay: 10,
            //list: []
        }
    }

    handleArray = (list) => {
        console.log(list);
        this.setState({
            list,
        }, this.remakeFileList);

    }

    remakeFileList = () => {
        this.handleStop();
        const {
            element,
            length,
            elementType,
            list
        } = this.state;
        element.initElements(length, elementType, list);
        this.setState({element});
    }

    remakeHeightList = () => {
        this.handleStop();
        const {
            element,
            length,
            //elementType
        } = this.state;
        //element.initElements(length, elementType);
        this.setState({element});
    }

    updateElementArr = (arr) => {
        const element = this.state.element;
        element.arr = arr;
        this.setState({
            element
        });
    }

    handleSorting = () => {
        const {
            sortType,
            delay
        } = this.state;
        this.sortInstance = this.sortFactory.getSort(sortType, this.state.element.arr, delay, this.updateElementArr, this.handleSorting);
        this.handleStop();
        this.sortInstance.start();
    }

    handleStop = () => {
        if(this.sortInstance) {
            this.sortInstance.stop();
        }
    }

    handleDelay = (delay) => {
        this.handleStop();
        this.setState({
            delay
        });
    }

    handleSortType = (sortType) => {
        this.handleStop();
        this.setState({
            sortType
        }, this.remakeHeightList);
    }

    render() {
        const {
            sorting,
            length,
            delay,
            element,
            sortType,
            elementType
        } = this.state;

        const {
            handleLength,
            handleSorting,
            handleStop,
            handleDelay,
            handleSortType,
            remakeHeightList,
            handleElemType
        } = this;

        return (
            <div className="App">
                <Header/>
                <Container
                    {...this.state}
                    {...this}
                />
            </div>
        );
    }
}

export default FilePage;
