import React, {Component} from 'react';
import SortType from "../filesort/SortType";
import ElementMakeType from "../filesort/ElementMakeType";
import {getSortName} from "../filesort/SortUtils";

class Setting extends Component {
    state = {
        fileList : []
    }

    onTypeChange = (e) => {
        this.props.handleSortType(parseInt(e.target.value));
    }

    onDelayChange = (e) => {
        let delay = parseInt(e.target.value) || 1;
        if (delay < 1) {
            delay = 1;
        }
        this.props.handleDelay(delay);
    }

    onStartClick = () => {
        this.props.handleSorting();
    }

    onStopClick = () => {
        this.props.handleStop();
    }

    onRemakeClick = () => {
        this.props.remakeHeightList();
    }

    SendArray = (list) => {
        console.log(list);
        this.props.handleArray(list);
    }

    afterFunc = (e) => {
        console.log(e);
        this.setState( {fileList: e});
        //console.log(this.state.fileList);
        //this.props.handleArray();
        <Hi number={this.state.fileList}></Hi>
    }

    handleChange(e){
        //const fileList = [];
        let file = e.target.files[0];
        let fileReader = new FileReader();
        let list;
        fileReader.onload = () => {
            console.log(fileReader.result);
            var text = fileReader.result
            var array = text.split(" ");
            console.log(array);
            for(var i=0; i<array.length; i++) {
                array[i] = Number(array[i]);
            }
            console.log(array);
            list = array;
            this.afterFunc(list);
        };
        fileReader.readAsText(file);
        this.props.handleArray(this.state.fileList);
        console.log(this.state.fileList);
    }

    render() {
        const sortingType = [];
        for (const type in SortType) {
            sortingType[SortType[type]] = getSortName(SortType[type]);
        }
        const elemType = [];
        for (const type in ElementMakeType) {
            elemType[ElementMakeType[type]] = type;
        }
        const {
            length,
            delay,
            sortType,
            elementType,
            list
        } = this.props;

        return (
            <div className="setting">
                <label htmlFor="type">Sort Type</label>
                <br/>
                <select name="type" onChange={this.onTypeChange} value={sortType}>
                    {sortingType.map((sort, index) => (
                        <option value={index} key={index}>{sort}</option>
                    ))}
                </select>
                <br/>
                <br/>

                <label htmlFor="delay">Delay</label>
                <br/>
                <input type="number" name="delay" value={delay} onChange={this.onDelayChange}/>ms
                <br/>
                <br/>
                <input type="button" value="Start" onClick={this.onStartClick}/>
                <input type="button" value="Stop" onClick={this.onStopClick}/>
                <input type="button" value="remake" onClick={this.onRemakeClick}/>
                <br/>
                <br/>
                <label htmlFor="fileopen">File Open</label>
                <br/>
                <input type= "file" accept='.txt' value={list} onChange={this.handleChange.bind(this)}/>

                <br/>
                <br/>
                <label htmlFor="filesave">File Save</label>
                <br/>
            </div>
        )
    }
}

export default Setting;
