import React from 'react';
import '../bubblesort/SortingVisualizer/SortingViz.css';
import {getBubbleSortAnimations} from '../bubblesort/SortingAlgorithms/BubbleSort.js'

// Change this value for the speed of the animations.
const ANIMATION_SPEED_MS = 1;
// Change this value for the number of bars (value) in the array.
//const NUMBER_OF_ARRAY_BARS = 200;
// This is the main color of the array bars.
const PRIMARY_COLOR = 'turquoise';
// This is the color of array bars that are being compared throughout the animations.
const SECONDARY_COLOR = 'red';

export default class SortingViz extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            array: []
        }
    }

    componentDidMount(){
        this.resetArray();
    }

    resetArray(){
        const array = [];
        for(let i=0;i<100;i++){
            array.push(i*2)
        }
        this.setState({array})
    }

    bubbleSort(){
        const [animations,sortArray] = getBubbleSortAnimations(this.state.array);
        for (let i = 0; i < animations.length; i++) {
            const isColorChange = (i % 4 === 0) || (i % 4 === 1);
            const arrayBars = document.getElementsByClassName('array-bar');
            if(isColorChange === true) {
                const color = (i % 4 === 0) ? SECONDARY_COLOR : PRIMARY_COLOR;
                const [barOneIndex, barTwoIndex] = animations[i];
                const barOneStyle = arrayBars[barOneIndex].style;
                const barTwoStyle = arrayBars[barTwoIndex].style;
                setTimeout(() => {
                    barOneStyle.backgroundColor = color;
                    barTwoStyle.backgroundColor = color;
                },i * ANIMATION_SPEED_MS);
            }
            else {
                const [barIndex, newHeight] = animations[i];
                if (barIndex === -1) {
                    continue;
                }
                const barStyle = arrayBars[barIndex].style;
                setTimeout(() => {
                    barStyle.height = `${newHeight}px`;
                },i * ANIMATION_SPEED_MS);
            }
        }
        // this.setState({array: sortArray})
        // const RESTORE_TIME = parseInt(ANIMATION_SPEED_MS*animations.length/2 + 3000);
        // setTimeout(() => this.restoreStoreButtons(), RESTORE_TIME);
    }

    afterFunc = (e) => {
        console.log(e);
        this.setState( {array: e});
        console.log(this.state.array);
        this.bubbleSort();
    }

    handleChange(e){
        let file = e.target.files[0];
        let fileReader = new FileReader();
        let list;
        fileReader.onload = () => {
            //console.log(fileReader.result);
            var text = fileReader.result
            var array = text.split(" ");
            console.log(array);
            for(var i=0; i<array.length; i++) {
                array[i] = 5*Number(array[i]);
            }
            //console.log(array);
            list = array;
            this.afterFunc(list);
        };
        fileReader.readAsText(file);
    }

    render(){
        const {array} = this.state;
        return(
            <div className="array-container">
            {array.map((value,idx) => (
                <div
                className="array-bar"
                 key={idx}
                 style={{height: `${value}px`}}
                 wid = {{width: `$3px`}}
                 ></div>
            ))}
            <br></br>
            <button onClick={() => this.bubbleSort()}>Bubble Sort</button>
            <input type= "file" accept='.txt' onChange={this.handleChange.bind(this)}/>
            </div>
        )
    }
}

function randomIntFromInterval(min, max){
    return Math.floor(Math.random() * (max-min+1) + min);
}

function arrayAreEqual(arrayOne,ArrayTwo){
    if(arrayOne.length !== ArrayTwo.length) return false;
    for(let i=0;i<arrayOne.length;i++){
        if(arrayOne[i] !== ArrayTwo[i]) return false;
    }
    return true;
}
